
public class Nivell2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double d = 7.1234;
		int numero;
		float f;
		String str;
		
		
		numero = (int)d;
		System.out.println(numero);
		
		f = (float)d;
		System.out.println(f);
		
		str = String.valueOf(d);
		System.out.print(str);	
	}
}
